#!/bin/bash
whois $1 | awk '/^Registrant Name:/{print "Registrant Name, "$3" "$4}/^Registrant Organization:/{print "Registrant Organization, "substr($0,index($0,$3))}/^Registrant Street:/{print "Registrant Street, "substr($0,index($0,$3))}/^Registrant City:/{print "Registrant City, "substr($0,index($0,$3))}/^Registrant State\/Province:/{print "Registrant State/Province, "substr($0,index($0,$3))}/^Registrant Postal Code:/{print "Registrant Postal Code, "substr($0,index($0,$4))}/^Registrant Country:/{print "Registrant Country, "$3}/^Registrant Phone:/{print "Registrant Phone, "$3}/^Registrant Phone Ext:/{print "Registrant Phone Ext:, "}/^Registrant Fax:/{print "Registrant Fax, "}/^Registrant Fax Ext:/{print "Registrant Fax Ext:, "}/^Registrant Email:/{print "Registrant Email, "$3}/^Admin Name:/{print "Admin Name, "$3" "$4}/^Admin Organization:/{print "Admin Organization, "substr($0,index($0,$3))}/^Admin Street:/{print "Admin Street, "substr($0,index($0,$3))}/^Admin City:/{print "Admin City, "substr($0,index($0,$3))}/^Admin State\/Province:/{print "Admin State/Province, "substr($0,index($0,$3))}/^Admin Postal Code:/{print "Admin Postal Code, "substr($0,index($0,$4))}/^Admin Country:/{print "Admin Country, "$3}/^Admin Phone:/{print "Admin Phone, "$3}/^Admin Phone Ext:/{print "Admin Phone Ext:, "}/^Admin Fax:/{print "Admin Fax, "}/^Admin Fax Ext:/{print "Admin Fax Ext:, "}/^Admin Email:/{print "Admin Email, "$3}/^Tech Name:/{print "Tech Name, "$3" "$4}/^Tech Organization:/{print "Tech Organization, "substr($0,index($0,$3))}/^Tech Street:/{print "Tech Street, "substr($0,index($0,$3))}/^Tech City:/{print "Tech City, "substr($0,index($0,$3))}/^Tech State\/Province:/{print "Tech State/Province, "substr($0,index($0,$3))}/^Tech Postal Code:/{print "Tech Postal Code, "substr($0,index($0,$4))}/^Tech Country:/{print "Tech Country, "$3}/^Tech Phone:/{print "Tech Phone, "$3}/^Tech Phone Ext:/{print "Tech Phone Ext:, "}/^Tech Fax:/{print "Tech Fax, "}/^Tech Fax Ext:/{print "Tech Fax Ext:, "}/^Tech Email:/{print "Tech Email, "$3}' > $1.csv
